import React, { useState } from 'react';

const mockCompanies = [
  {
    name: 'Techify',
    logo: '/assets/company-logos/google-icon-logo-svgrepo-com.svg',
    industry: 'Technology',
    description: 'Innovating the future of search and AI.'
  },
  {
    name: 'Streamly',
    logo: '/assets/company-logos/netflix-2-logo-svgrepo-com.svg',
    industry: 'Entertainment',
    description: 'Streaming movies and shows worldwide.'
  },
  {
    name: 'FastFoodies',
    logo: '/assets/company-logos/mcdonald-s-15-logo-svgrepo-com.svg',
    industry: 'Food & Beverage',
    description: 'Global leader in quick service restaurants.'
  },
  {
    name: 'FitLife',
    logo: '/assets/company-logos/nike-3-logo-svgrepo-com.svg',
    industry: 'Sportswear',
    description: 'Empowering athletes everywhere.'
  },
  {
    name: 'SocialBee',
    logo: '/assets/company-logos/instagram-logo-svgrepo-com.svg',
    industry: 'Social Media',
    description: 'Connecting people through photos and stories.'
  },
  {
    name: 'AutoDrive',
    logo: '/assets/company-logos/bmw-logo-svgrepo-com.svg',
    industry: 'Automotive',
    description: 'Driving innovation in mobility.'
  },
  {
    name: 'Soundify',
    logo: '/assets/company-logos/spotify-1-logo-svgrepo-com.svg',
    industry: 'Music',
    description: 'Music for everyone, everywhere.'
  },
  {
    name: 'CloudSync',
    logo: '/assets/company-logos/microsoft-logo-svgrepo-com.svg',
    industry: 'Cloud Computing',
    description: 'Empowering every person and organization.'
  },
];

export default function Companies() {
  const [search, setSearch] = useState('');
  const filtered = mockCompanies.filter(c =>
    c.name.toLowerCase().includes(search.toLowerCase()) ||
    c.industry.toLowerCase().includes(search.toLowerCase())
  );
  return (
    <div className="max-w-5xl mx-auto py-16 px-4">
      <h1 className="text-3xl font-bold mb-6 text-center">Browse Companies</h1>
      <div className="mb-8 flex justify-center">
        <input
          type="text"
          value={search}
          onChange={e => setSearch(e.target.value)}
          placeholder="Search companies or industries..."
          className="input w-full max-w-md px-4 py-2 border rounded-lg shadow-sm"
        />
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {filtered.length === 0 ? (
          <div className="col-span-full text-center text-gray-500">No companies found.</div>
        ) : (
          filtered.map((company, i) => (
            <div
              key={company.name}
              className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6 flex flex-col items-center text-center transform transition duration-500 hover:-translate-y-2 hover:scale-105 animate-fadeInUp"
              style={{ animationDelay: `${i * 0.08 + 0.1}s`, animationFillMode: 'both' }}
            >
              <img src={company.logo} alt={company.name} className="h-16 w-16 mb-4 rounded-full object-contain bg-gray-100" />
              <div className="font-semibold text-lg mb-1">{company.name}</div>
              <div className="text-primary-600 dark:text-primary-300 text-sm mb-2">{company.industry}</div>
              <div className="text-gray-500 dark:text-gray-300 text-xs">{company.description}</div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
