import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Navigate } from 'react-router-dom';

const AdminPanel = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('users');

  if (!user || user.role !== 'admin') {
    return <Navigate to="/" replace />;
  }

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Admin Panel</h1>
      <div className="flex space-x-4 mb-8">
        <button
          className={`px-4 py-2 rounded ${activeTab === 'users' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
          onClick={() => setActiveTab('users')}
        >
          Users
        </button>
        <button
          className={`px-4 py-2 rounded ${activeTab === 'jobs' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
          onClick={() => setActiveTab('jobs')}
        >
          Jobs
        </button>
        <button
          className={`px-4 py-2 rounded ${activeTab === 'campaigns' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
          onClick={() => setActiveTab('campaigns')}
        >
          Campaigns
        </button>
      </div>
      <div>
        {activeTab === 'users' && (
          <div>
            <h2 className="text-xl font-semibold mb-4">Manage Users</h2>
            {/* TODO: List, edit, and delete users */}
            <p>Users management coming soon...</p>
          </div>
        )}
        {activeTab === 'jobs' && (
          <div>
            <h2 className="text-xl font-semibold mb-4">Manage Jobs</h2>
            {/* TODO: List, edit, and delete jobs */}
            <p>Jobs management coming soon...</p>
          </div>
        )}
        {activeTab === 'campaigns' && (
          <div>
            <h2 className="text-xl font-semibold mb-4">Manage Campaigns</h2>
            {/* TODO: List, edit, and delete campaigns */}
            <p>Campaigns management coming soon...</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
