import React, { useState, useEffect } from 'react';
import { Search, MapPin, Briefcase, DollarSign, Clock, Building, Loader2 } from 'lucide-react';
import { useMode } from '../contexts/ModeContext';
import { jobAPI } from '../utils/api';
import { useLocation, useNavigate } from 'react-router-dom';

const Jobs = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [location, setLocation] = useState('');
  const [role, setRole] = useState('');
  const { isDarkMode } = useMode();

  // Mock job data
  const jobs = [
    {
      id: 1,
      title: 'Senior Full Stack Engineer',
      company: 'TechCorp',
      location: 'San Francisco, CA',
      type: 'Full-time',
      salary: '$120k - $180k',
      experience: '3-5 years',
      logo: '/assets/companies/techcorp.svg',
      description: 'We are looking for a Senior Full Stack Engineer to join our growing team...',
      skills: ['React', 'Node.js', 'Python', 'AWS'],
      posted: '2 days ago'
    },
    {
      id: 2,
      title: 'Product Manager',
      company: 'StartupXYZ',
      location: 'Remote',
      type: 'Full-time',
      salary: '$100k - $150k',
      experience: '2-4 years',
      logo: '/assets/companies/startupxyz.svg',
      description: 'Join our product team and help shape the future of our platform...',
      skills: ['Product Strategy', 'User Research', 'Analytics'],
      posted: '1 week ago'
    },
    {
      id: 3,
      title: 'Frontend Developer',
      company: 'InnovateLab',
      location: 'New York, NY',
      type: 'Full-time',
      salary: '$90k - $130k',
      experience: '1-3 years',
      logo: '/assets/companies/innovatelab.svg',
      description: 'Build beautiful, responsive user interfaces for our web applications...',
      skills: ['React', 'TypeScript', 'CSS', 'UI/UX'],
      posted: '3 days ago'
    }
  ];

  return (
    <div className="min-h-screen bg-background-light dark:bg-background-dark transition-colors duration-200">
      {/* Search Header */}
      <div className={`border-b transition-colors duration-200 ${
        isDarkMode ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-200'
      }`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <h1 className={`text-3xl font-bold mb-6 transition-colors duration-200 ${
            isDarkMode ? 'text-white' : 'text-gray-900'
          }`}>Find your next job</h1>

          {/* Search Bar */}
          <div className="flex flex-col md:flex-row gap-4 mb-6">
            <div className="flex-1 relative">
              <Search className={`absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 transition-colors duration-200 ${
                isDarkMode ? 'text-gray-400' : 'text-gray-400'
              }`} />
              <input
                type="text"
                placeholder="Search jobs, companies, skills..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className={`w-full pl-10 pr-4 py-3 border rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 transition-colors duration-200 ${
                  isDarkMode 
                    ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                    : 'border-gray-300'
                }`}
              />
            </div>
            <div className="flex gap-4">
              <div className="relative">
                <MapPin className={`absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 transition-colors duration-200 ${
                  isDarkMode ? 'text-gray-400' : 'text-gray-400'
                }`} />
                <input
                  type="text"
                  placeholder="Location"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  className={`pl-10 pr-4 py-3 border rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 transition-colors duration-200 ${
                    isDarkMode 
                      ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                      : 'border-gray-300'
                  }`}
                />
              </div>
              <select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                className={`px-4 py-3 border rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 transition-colors duration-200 ${
                  isDarkMode 
                    ? 'bg-gray-700 border-gray-600 text-white' 
                    : 'border-gray-300'
                }`}
              >
                <option value="">All Roles</option>
                <option value="engineer">Engineer</option>
                <option value="product">Product</option>
                <option value="design">Design</option>
                <option value="marketing">Marketing</option>
              </select>
            </div>
          </div>

          {/* Quick Filters */}
          <div className="flex flex-wrap gap-2">
            <button className={`px-4 py-2 rounded-full text-sm font-medium transition-colors duration-200 ${
              isDarkMode 
                ? 'bg-primary-900 text-primary-200' 
                : 'bg-primary-100 text-primary-700'
            }`}>
              Remote
            </button>
            <button className={`px-4 py-2 rounded-full text-sm font-medium hover:bg-primary-50 transition-colors duration-200 ${
              isDarkMode 
                ? 'bg-gray-700 text-gray-200 hover:bg-gray-600' 
                : 'bg-gray-100 text-gray-700'
            }`}>
              Full-time
            </button>
            <button className={`px-4 py-2 rounded-full text-sm font-medium hover:bg-primary-50 transition-colors duration-200 ${
              isDarkMode 
                ? 'bg-gray-700 text-gray-200 hover:bg-gray-600' 
                : 'bg-gray-100 text-gray-700'
            }`}>
              Part-time
            </button>
            <button className={`px-4 py-2 rounded-full text-sm font-medium hover:bg-primary-50 transition-colors duration-200 ${
              isDarkMode 
                ? 'bg-gray-700 text-gray-200 hover:bg-gray-600' 
                : 'bg-gray-100 text-gray-700'
            }`}>
              Contract
            </button>
          </div>
        </div>
      </div>

      {/* Job Listings */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid gap-6">
          {jobs.map((job) => (
            <div key={job.id} className={`rounded-lg border p-6 hover:shadow-md transition-all duration-200 ${
              isDarkMode 
                ? 'bg-gray-800 border-gray-700 hover:shadow-lg hover:shadow-gray-900/50' 
                : 'bg-white border-gray-200'
            }`}>
              <div className="flex items-start justify-between">
                <div className="flex items-start space-x-4">
                  <div className={`w-12 h-12 rounded-lg flex items-center justify-center transition-colors duration-200 ${
                    isDarkMode ? 'bg-gray-700' : 'bg-gray-100'
                  }`}>
                    <Building className={`h-6 w-6 transition-colors duration-200 ${
                      isDarkMode ? 'text-gray-400' : 'text-gray-500'
                    }`} />
                  </div>
                  <div className="flex-1">
                    <h3 className={`text-lg font-semibold mb-1 transition-colors duration-200 ${
                      isDarkMode ? 'text-white' : 'text-gray-900'
                    }`}>{job.title}</h3>
                    <p className={`mb-2 transition-colors duration-200 ${
                      isDarkMode ? 'text-gray-300' : 'text-gray-600'
                    }`}>{job.company}</p>

                    <div className={`flex items-center space-x-4 text-sm mb-3 transition-colors duration-200 ${
                      isDarkMode ? 'text-gray-400' : 'text-gray-500'
                    }`}>
                      <div className="flex items-center">
                        <MapPin className="h-4 w-4 mr-1" />
                        {job.location}
                      </div>
                      <div className="flex items-center">
                        <Clock className="h-4 w-4 mr-1" />
                        {job.type}
                      </div>
                      <div className="flex items-center">
                        <DollarSign className="h-4 w-4 mr-1" />
                        {job.salary}
                      </div>
                      <div className="flex items-center">
                        <Briefcase className="h-4 w-4 mr-1" />
                        {job.experience}
                      </div>
                    </div>

                    <p className={`mb-3 transition-colors duration-200 ${
                      isDarkMode ? 'text-gray-300' : 'text-gray-600'
                    }`}>{job.description}</p>

                    <div className="flex flex-wrap gap-2 mb-4">
                      {job.skills.map((skill, index) => (
                        <span
                          key={index}
                          className={`px-3 py-1 rounded-full text-sm transition-colors duration-200 ${
                            isDarkMode 
                              ? 'bg-primary-900 text-primary-200' 
                              : 'bg-primary-50 text-primary-700'
                          }`}
                        >
                          {skill}
                        </span>
                      ))}
                    </div>

                    <div className="flex items-center justify-between">
                      <span className={`text-sm transition-colors duration-200 ${
                        isDarkMode ? 'text-gray-400' : 'text-gray-500'
                      }`}>Posted {job.posted}</span>
                      <button className="btn-primary px-6 py-2">
                        Apply Now
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Load More */}
        <div className="text-center mt-8">
          <button className="btn-secondary px-8 py-3">
            Load More Jobs
          </button>
        </div>
      </div>
    </div>
  );
};

export default Jobs;
